(340, 'BiografÃ­as de MontaÃ±istas', '<table border="0" widht="720" align="center">\r\n    <tbody>\r\n        <tr>\r\n            <td valign="bottom" height="100" widht="25%"><a href="?id=339"><img width="130" vspace="5" hspace="10" height="150" border="0" align="left" title="Sir Edmund Hillary" alt="" src="img/biografias/menu_edmund_hillary.jpg" /></a></td>\r\n            <td valign="bottom" height="100" widht="25%"><a href="?id=344"><img width="130" vspace="5" hspace="10" height="150" border="0" align="left" title="Ivan Vallejo" alt="" src="img/biografias/menu_ivan_vallejo.jpg" /></a></td>\r\n            <td valign="bottom" height="100" widht="25%"><a href="?id=345"><img width="130" vspace="5" hspace="10" height="150" border="0" align="left" title="Jose Antonio Delgado" alt="" src="img/biografias/menu_jose_antonio_delgado.jpg" /></a></td>\r\n            <td valign="bottom" height="100" widht="25%"><a href="?id=349"><img width="130" vspace="5" hspace="10" height="150" border="0" align="left" title="Walter Bonatti" alt="" src="img/biografias/menu_walter_bonatti.jpg" /></a></td>\r\n        </tr>\r\n        <tr>\r\n            <td widht="25%" class="fotos"><a href="?id=339">Sir Edmund Hillary</a></td>\r\n            <td widht="25%" class="fotos"><a href="?id=344">Ivan Vallejo</a></td>\r\n            <td widht="25%" class="fotos"><a href="?id=345">Jose Antonio Delgado</a></td>\r\n            <td widht="25%" class="fotos"><a href="?id=349">Walter Bonatti</a></td>\r\n        </tr>\r\n        <tr>\r\n            <td valign="bottom" height="100" widht="25%"><a href="?id=346"><img width="130" vspace="5" hspace="10" height="150" border="0" align="left" title="Ram&oacute;n Blanco" alt="" src="img/biografias/menu_ramon_blanco.jpg" /></a></td>\r\n            <td valign="bottom" height="100" widht="25%"><a href="?id=347"><img width="130" vspace="5" hspace="10" height="150" border="0" align="left" title="Reinhold Messner" alt="" src="img/biografias/menu_reinhold_messner.jpg" /></a></td>\r\n            <td valign="bottom" height="100" widht="25%"><a href="?id=348"><img width="130" vspace="5" hspace="10" height="150" border="0" align="left" title="Tenzing Norgay" alt="" src="img/biografias/menu_tenzing_norgay.png" /></a></td>\r\n            <td valign="bottom" height="100" widht="25%"><a href="?id=343"><img width="130" vspace="5" hspace="10" height="150" border="0" align="left" title="Gerlinde Kaltenbrunner" alt="" src="img/biografias/menu_gerlinde_kaltenbrunner.jpg" /></a></td>\r\n        </tr>\r\n        <tr>\r\n            <td widht="25%" class="fotos"><a href="?id=346">Ram&oacute;n Blanco</a></td>\r\n            <td widht="25%" class="fotos"><a href="?id=347">Reinhold Messner</a></td>\r\n            <td widht="25%" class="fotos"><a href="?id=348">Tenzing Norgay</a></td>\r\n            <td widht="25%" class="fotos"><a href="?id=343">Gerlinde Kaltenbrunner</a></td>\r\n        </tr>\r\n        <tr>\r\n            <td valign="bottom" height="100" widht="25%"><a href="?id=342"><img width="130" vspace="5" hspace="10" height="150" border="0" align="left" title="Edurne Pasaban" alt="" src="img/biografias/menu_edurne_pasaban.jpg" /></a></td>\r\n            <td valign="bottom" height="100" widht="25%"><a href="?id=341"><img width="130" vspace="5" hspace="10" height="150" border="0" align="left" title="Ed Viesturs" alt="" src="img/biografias/menu_ed_viesturs.jpg" /></a></td>\r\n            <td valign="bottom" height="100" widht="25%"><a href="?id=352"><img width="130" vspace="5" hspace="10" height="150" border="0" align="left" title="Fredrik Ericsson" alt="" src="img/biografias/menu_fredrik_ericsson.jpg" /></a></td>\r\n            <td valign="bottom" height="100" widht="25%"><a href="?id=350"><img width="130" vspace="5" hspace="10" height="150" border="0" align="left" title="Juanito Oiarzabal" alt="" src="img/biografias/menu_juanito_oiarzabal.jpg" /></a></td>\r\n        </tr>\r\n        <tr>\r\n            <td widht="25%" class="fotos"><a href="?id=342">Edurne Pasaban</a></td>\r\n            <td widht="25%" class="fotos"><a href="?id=341">Ed Viesturs</a></td>\r\n            <td widht="25%" class="fotos"><a href="?id=352">Fredrik Ericsson</a></td>\r\n            <td widht="25%" class="fotos"><a href="?id=350">Juanito Oiarzabal</a></td>\r\n        </tr>\r\n        <tr>\r\n            <td valign="bottom" height="100" widht="25%"><a href="?id=351"><img width="130" vspace="5" hspace="10" height="150" border="0" align="left" title="Carlos Soria" alt="" src="img/biografias/menu_carlos_soria.jpg" /></a></td>\r\n            <td valign="bottom" height="100" widht="25%">&nbsp;</td>\r\n            <td valign="bottom" height="100" widht="25%">&nbsp;</td>\r\n            <td valign="bottom" height="100" widht="25%">&nbsp;</td>\r\n        </tr>\r\n        <tr>\r\n            <td widht="25%" class="fotos"><a href="?id=351">Carlos Soria</a></td>\r\n            <td widht="25%" class="fotos">&nbsp;</td>\r\n            <td widht="25%" class="fotos">&nbsp;</td>\r\n            <td widht="25%" class="fotos">&nbsp;</td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p>&nbsp;</p>', 'f', '1', 6, 1, 150, 1, '2012-03-31 11:22:16', 0, 0),
(334, 'Sierra de la Culata (MÃ©rida)', '<table border="0" width="100%">\r\n    <tbody>\r\n        <tr>\r\n            <td valign="bottom" height="100" widht="25%">\r\n            <div align="center"><a href="http://www.cumbres2000.com/img/mapas/mapa_culata_topografico.jpg" target="_blank"><img border="0" width="300" height="225" src="img/mapas/mapas_culata_topografico.jpg" alt="" title="Topografico de la Culata" /></a></div>\r\n            </td>\r\n            <td valign="bottom" height="100" widht="25%">\r\n            <div align="center"><a href="http://www.cumbres2000.com/img/mapas/sector_pan_azucar.jpg" target="_blank"><img border="0" width="300" height="225" src="img/mapas/mapas_culata_rutas.jpg" alt="" title="Sector Pan de Azucar" /></a></div>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td class="fotos_mapas" widht="25%"><a href="http://www.cumbres2000.com/img/mapas/mapa_culata_topografico.jpg" target="_blank">Topografico</a></td>\r\n            <td class="fotos_mapas" widht="25%"><a href="http://www.cumbres2000.com/img/mapas/sector_pan_azucar.jpg" target="_blank">Sector Pan de Azucar</a></td>\r\n        </tr>\r\n        <tr>\r\n            <td valign="bottom" height="100" widht="25%">\r\n            <div align="center"><a href="http://www.mediafire.com/?3fy4b3ew6gyuh3e" target="_blank"><img border="0" width="300" height="225" src="img/mapas/mapas_culata_mediafire.jpg" alt="" title="Descarga los mapas en Mediafire" /></a></div>\r\n            </td>\r\n            <td valign="bottom" height="100" widht="25%">\r\n            <div align="center"><a href="http://www.cumbres2000.com/img/mapas/mapa_musui.jpg" target="_blank"><img border="0" width="300" height="225" src="img/mapas/mapas_musui.jpg" alt="" title="La Culata - La Musui" /></a></div>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td class="fotos_mapas" widht="25%"><a href="http://www.mediafire.com/?3fy4b3ew6gyuh3e" target="_blank">Descarga los mapas en Mediafire</a></td>\r\n            <td class="fotos_mapas" widht="25%"><a href="http://www.cumbres2000.com/img/mapas/mapa_musui.jpg" target="_blank">La Culata - La Musui</a></td>\r\n        </tr>\r\n        <tr>\r\n            <td valign="bottom" height="100" widht="25%">\r\n            <div align="center"><a href="http://www.cumbres2000.com/img/mapas/mapa_pico_nevados_completa.jpg" target="_blank"><img border="0" width="300" height="225" src="img/mapas/mapas_pico_nevados_completa.jpg" alt="" title="Ruta de Ascenso al Pico Los Nevados" /></a></div>\r\n            </td>\r\n            <td valign="bottom" height="100" widht="25%">\r\n            <div align="center"><a href="http://www.cumbres2000.com/img/mapas/mapa_pico_nevados_ascenso.jpg" target="_blank"><img border="0" width="300" height="225" src="img/mapas/mapas_pico_nevados_ascenso.jpg" alt="" title="Ascenso a la cumbre del Pico Los Nevados" /></a></div>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td class="fotos_mapas" widht="25%"><a href="http://www.cumbres2000.com/img/mapas/mapa_pico_nevados_completa.jpg" target="_blank">Pico Los Nevados: Ruta de Ascenso Completa<br />\r\n            </a></td>\r\n            <td class="fotos_mapas" widht="25%"><a href="http://www.cumbres2000.com/img/mapas/mapa_pico_nevados_ascenso.jpg" target="_blank">Pico Los Nevados: Ascenso a la cumbre</a></td>\r\n        </tr>\r\n        <tr>\r\n            <td valign="bottom" height="100" widht="25%">\r\n            <div align="center"><a href="http://www.cumbres2000.com/img/mapas/mapa_pico_piedras_blancas.jpg" target="_blank"><img border="0" width="300" height="225" src="img/mapas/mapas_pico_piedras_blancas.jpg" alt="" title="Ascenso a la cumbre" /></a></div>\r\n            </td>\r\n            <td valign="bottom" height="100" widht="25%">\r\n            <div align="center"><a href="http://www.cumbres2000.com/img/mapas/mapa_pico_buitre.jpg" target="_blank"><img border="0" width="300" height="225" src="img/mapas/mapas_pico_buitre.jpg" alt="" title="Ruta de Ascenso al Pico El Buitre" /></a></div>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td class="fotos_mapas" widht="25%"><a href="http://www.cumbres2000.com/img/mapas/mapas_pico_piedras_blancas.jpg" target="_blank">Pico Piedras Blancas: Ascenso a la cumbre</a></td>\r\n            <td class="fotos_mapas" widht="25%"><a href="http://www.cumbres2000.com/img/mapas/mapa_pico_buitre.jpg" target="_blank">Pico El Buitre: Ruta de Ascenso</a></td>\r\n        </tr>\r\n        <tr>\r\n            <td valign="bottom" height="100" widht="25%">\r\n            <div align="center"><a href="http://www.cumbres2000.com/img/mapas/mapa_pico_agua_blanca.jpg" target="_blank"><img border="0" width="300" height="225" src="img/mapas/mapas_pico_agua_blanca.jpg" alt="" title="Pico Agua Blanca: Ruta de Ascenso" /></a></div>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td class="fotos_mapas" widht="25%"><a href="http://www.cumbres2000.com/img/mapas/mapa_pico_agua_blanca.jpg" target="_blank">Pico Agua Blanca: Ruta de Ascenso</a></td>\r\n        </tr>\r\n        <tr>\r\n        </tr>\r\n    </tbody>\r\n</table>', 'f', '1', 4, 1, 327, 1, '2012-03-24 13:15:22', 0, 0),
(341, 'BiografÃ­a: Ed Viesturs', '<p><img vspace="5" hspace="5" align="right" title="" alt="" src="img/biografias/ed_viesturs_01.jpg" />Edmund Viesturs, conocido como Ed Viesturs, (Fort Wayne, Indiana, 22 de junio de 1959), es uno de los principales monta&ntilde;istas de grandes alturas del mundo. Es el primer norteamericano, y 12&ordm; en el ranking mundial en alcanzar las 14 monta&ntilde;as de m&aacute;s de 8000 metros, conocidas como ochomiles. Todas las cumbres fueron alcanzadas sin equipo de ox&iacute;geno, siendo la sexta persona en lograrlo. Viesturs tambi&eacute;n ha alcanzado el monte Everest siete veces, un logro que, exceptuando a sherpas, solo han logrado Peter Athans y Dave Hahn.</p>\r\n<p>Nacido en Fort Wayne (Indiana), y tras crecer en Rockford, Illinois, Viesturs se mud&oacute; a Seattle, Washington en 1977 para asistir a la Universidad de Washington. Comenz&oacute; la carrera de monta&ntilde;ista en el monte Rainier.</p>\r\n<p>Viesturs estuvo en el equipo de IMAX durante el desastre del Everest de 1996. Viesturs fue la estrella del film del Everest de IMAX. La filmaci&oacute;n fue retrasada por el golpe de una tormenta de nieve y el grupo de IMAX pospuso la filmaci&oacute;n de la ayuda.</p>\r\n<p>Su inter&eacute;s por el Himalaya comenz&oacute; en la universidad leyendo el relato de Maurice Herzog del primer ascenso al Annapurna. Luego del ascenso al Kanchenjunga en 1989, al Monte Everest en 1990, y al K2 en 1992, Viesturs fue habilitado a convertirse en un gu&iacute;a de monta&ntilde;a internacional y obtener sponsor de alpinismo de tiempo completo.</p>\r\n<p>Viesturs escal&oacute; en compa&ntilde;&iacute;a de VeikkaGustafsson.</p>\r\n<p>Particip&oacute; del filme L&iacute;mite Vertical</p>\r\n<p><img vspace="5" hspace="5" align="right" title="" alt="" src="img/biografias/ed_viesturs_02.jpg" />Lista de sus mayores ascensos</p>\r\n<p>1987 &ndash; Everest<br />\r\n1989 - Kanchenjunga<br />\r\n1990 - Everest<br />\r\n1991 - Everest<br />\r\n1992 - K2 - con Scott Fischer<br />\r\n1993 - Everest - lo intent&oacute;, pero no lleg&oacute; a la cima<br />\r\n1994 - Everest &ndash; Gui&oacute; la expedici&oacute;n comercial de Rob Hall<br />\r\n1994 - Lhotse<br />\r\n1994 - Cho Oyu<br />\r\n1995 - Makalu<br />\r\n1995 - Gasherbrum II<br />\r\n1995 - Gasherbrum I<br />\r\n1996 - Everest &ndash; form&oacute; parte de la expedici&oacute;n de IMAX de David Breashears, sin suplemento de ox&iacute;geno<br />\r\n1997 - Everest &ndash; Expedici&oacute;n PBS Nova<br />\r\n1997 - Broad Peak<br />\r\n1999 - Manaslu<br />\r\n1999 - Dhaulagiri<br />\r\n2000 - Shishapangma<br />\r\n2000 - Annapurna - lo intent&oacute;, pero no lleg&oacute; a la cima<br />\r\n2001 - Nanga Parbat - lo intent&oacute;, pero no lleg&oacute; a la cima<br />\r\n2002 - Annapurna - lo intent&oacute;, pero no lleg&oacute; a la cima<br />\r\n2003 - Nanga Parbat, Broad Peak<br />\r\n2004 - Everest &ndash; Form&oacute; parte de una expedici&oacute;n para el film acerca del desastre del Everest de 1996, dirigido por Stephen Daldry<br />\r\n2005 - ChoOyu &ndash; lo intent&oacute;, pero no lleg&oacute; a la cima en su esfuerzo pasa salvar a su amigo Jimmy Chin (experto fot&oacute;grafo alpinista) de un edema cerebral<br />\r\n2005 - Annapurna<br />\r\n2009 - Everest por s&eacute;ptima vez</p>\r\n<p><a href="?id=340"><strong>Volver a Biograf&iacute;as</strong></a></p>', 'f', '1', 8, 1, NULL, 1, '2012-03-31 11:29:06', 0, 0),
(126, 'Teta de Niquitao', '<p>Es la monta&ntilde;a m&aacute;s emblem&aacute;tica para los trujillanos y surge imponente a m&aacute;s de cuatro mil metros (4006 msnm) en la Cordillera de Trujillo. Puede v&eacute;rsele desde lejanos lugares con diversas formas, si bien entre ellas, siempre destaca para los ojos avezados de los campesinos como parte de una geograf&iacute;a sensual y femenina: Teta.</p>\r\n<table cellspacing="0" cellpadding="0" align="center">\r\n    <tbody>\r\n        <tr>\r\n            <td><img width="720" vspace="5" hspace="10" height="350" border="0" align="left" src="img/rutas/teta_niquitao_port.jpg" title="La cumbre a la vista" alt="" /></td>\r\n        </tr>\r\n        <tr>\r\n            <td class="fotos_contenido_port">La cumbre a la vista</td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<table width="170" cellspacing="0" cellpadding="0" align="right">\r\n    <tbody>\r\n        <tr>\r\n            <td style="text-align: center;"><img width="160" vspace="5" hspace="10" height="160" border="0" align="middle" src="img/rutas/teta_niquitao_01.jpg" title="Iglesia de Niquitao" alt="" /></td>\r\n        </tr>\r\n        <tr>\r\n            <td class="fotos_contenido_lat">Iglesia de Niquitao</td>\r\n        </tr>\r\n        <tr>\r\n            <td align="center"><img width="160" vspace="5" hspace="10" height="160" border="0" align="middle" src="img/rutas/teta_niquitao_02.jpg" title="Puente Jose Felix Ribas" alt="" /></td>\r\n        </tr>\r\n        <tr>\r\n            <td class="fotos_contenido_lat">Puente Jose Felix Ribas</td>\r\n        </tr>\r\n        <tr>\r\n            <td style="text-align: center;"><img width="160" vspace="5" hspace="10" height="160" border="0" align="middle" src="img/rutas/teta_niquitao_03.jpg" title="Casas a la orilla de la carretera" alt="" /></td>\r\n        </tr>\r\n        <tr>\r\n            <td class="fotos_contenido_lat">Casas a la orilla de la carretera</td>\r\n        </tr>\r\n        <tr>\r\n            <td style="text-align: center;"><img width="160" vspace="5" hspace="10" height="160" border="0" align="middle" src="img/rutas/teta_niquitao_04.jpg" title="Teta de Niquitao al amanecer" alt="" /></td>\r\n        </tr>\r\n        <tr>\r\n            <td class="fotos_contenido_lat">Teta de Niquitao al amanecer</td>\r\n        </tr>\r\n        <tr>\r\n            <td style="text-align: center;"><img width="160" vspace="5" hspace="10" height="160" border="0" align="middle" src="img/rutas/teta_niquitao_05.jpg" title="Cumbre de la Teta de Niquitao" alt="" /></td>\r\n        </tr>\r\n        <tr>\r\n            <td class="fotos_contenido_lat">Cumbre de la Teta de Niquitao</td>\r\n        </tr>\r\n        <tr>\r\n            <td style="text-align: center;"><img width="160" vspace="5" hspace="10" height="160" border="0" align="middle" src="img/rutas/teta_niquitao_06.jpg" title="Laguna Las Marias" alt="" /></td>\r\n        </tr>\r\n        <tr>\r\n            <td class="fotos_contenido_lat">Laguna Las Marias</td>\r\n        </tr>\r\n        <tr>\r\n            <td style="text-align: center;"><img width="160" vspace="5" hspace="10" height="160" border="0" align="middle" src="img/rutas/teta_niquitao_07.jpg" title="Sembradio de papas" alt="" /></td>\r\n        </tr>\r\n        <tr>\r\n            <td class="fotos_contenido_lat">Sembradio de papas</td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p><strong>Creaci&oacute;n del Monumento Natural Teta de Niquitao</strong></p>\r\n<p>Fue decretado Monumento Natural el 4 de septiembre de 1996 seg&uacute;n Decreto N&ordm; 1473. Gaceta Oficial N&ordm; 36.063 de fecha 11 de octubre de 1996, con una extensi&oacute;n de 180.000 hect&aacute;reas.</p>\r\n<p><strong>Ubicaci&oacute;n</strong></p>\r\n<p>El Monumento Natural Teta de Niquitao se encuentra al sureste del Estado Trujillo, espec&iacute;ficamente en el Municipio Bocon&oacute;.</p>\r\n<p><strong>V&iacute;as de Acceso</strong></p>\r\n<p>Pueblos cercanos como Cabimb&uacute;, Niquitao, Tost&oacute;s, La Quebrada, Tu&ntilde;ame, Santiago o San L&aacute;zaro, sirven de marco y partida para su conquista. Desde los dos primeros, parten la mayor&iacute;a de las excursiones, a pi&eacute; o en veh&iacute;culos de doble tracci&oacute;n. Para llegar con autom&oacute;vil se debe tomar la v&iacute;a Bocon&oacute;-Niquitao-Las Mesitas o la v&iacute;a Bocon&oacute;-Tost&oacute;s-Cabimb&uacute;. A pi&eacute; desde Loma Tendida o Rurales de Cabimb&uacute;, puede subirse a la Laguna Mar&iacute;a Luisa en un tiempo de cinco a siete horas, seg&uacute;n el paso de los caminantes.</p>\r\n<p>La temperatura oscila entre los 3 &ordm;C y 16 &ordm;C. La temporada m&aacute;s recomendable para visitarla es entre los meses de diciembre y enero, por la ausencia de lluvias y el florecimiento de los frailejones, uno de sus atractivos.</p>\r\n<p><strong>Niquitao</strong></p>\r\n<p>El recorrido mas com&uacute;n o mas frecuente comienza por el pueblo de Niquitao, que se encuentra a 25 Km. de Bocon&oacute; y debe su nombre a un legendario cacique ind&iacute;gena. Fue fundado en 1626 y es conocido como un pueblo glorioso, porque su historia as&iacute; lo demuestra. La Batalla de Niquitao es una de las muestras m&aacute;s impactante del hero&iacute;smo del pueblo de Niquitao y de nuestros patriotas, quienes luchaban de manera desigual contra el poder&iacute;o espa&ntilde;ol, durante la &eacute;poca de la colonia. Niquitao a&uacute;n conserva gran parte de la arquitectura de la &eacute;poca. Es un lugar tranquilo, de gente amable y dispuesta a abrirles las puertas de su hogar a los turistas. Es un poblado elevado a 1937 msnm y se ubica en una meseta al pie de las monta&ntilde;as Estallij&uacute; a 100 metros encima del lecho del r&iacute;o Burate. Son cuatro calles principales y unas cuantas transversales que bien se puede recorrer a pi&eacute; con una agradable temperatura promedio de 16 &ordm;C.</p>\r\n<p><strong>Las Mesitas</strong></p>\r\n<p>No se tiene fecha exacta de su fundaci&oacute;n. Solo se conoce que fue decretada como parroquia civil en el a&ntilde;o 1854 y parroquia eclesi&aacute;stica en el a&ntilde;o 1856. Esta comarca ubicada entre riscos y quebradas fue ocupada en la &eacute;poca prehisp&aacute;nica por la tribu de los Vis&uacute;n, los Chejend&eacute; y parte de los Cambib&uacute;es.</p>\r\n<p>Situada a una altura de 2.190 msnm, y una temperatura interanual de 14&ordm; C, y distan 42 kil&oacute;metros, aproximadamente de Bocon&oacute;. Su gente de esp&iacute;ritu emprendedor, es la viva expresi&oacute;n de la bondad y el afecto de los trujillanos. Es una zona especialmente para las actividades de turismo de monta&ntilde;a, aventura y excursionismo. El parque natural de mayor importancia es el p&aacute;ramo del Guirigay, triangulo lim&iacute;trofe entre los estados M&eacute;rida, Trujillo y Barinas. Posee una poblaci&oacute;n aproximada de 5.000 habitantes. Posee 2 r&iacute;os y 23 quebradas, en casi todas existen truchas. La Agricultura es la base econ&oacute;mica de las Mesitas, el desarrollo econ&oacute;mico de esta comarca proviene especialmente de la papa y zanahoria, cultivos que han sustituido al trigo y al ma&iacute;z. Gran cantidad de productos agr&iacute;colas son sacados a diario a los mercados de grandes ciudades, tambi&eacute;n cuenta con una gran producci&oacute;n de claveles, f&aacute;ciles de obtener en el trayecto de la v&iacute;a. Es de hacer notar que entre Niquitao y las Mesitas, se encuentra el primer viaducto agr&iacute;cola de Venezuela, el m&aacute;s alto sobre una quebrada y el &uacute;nico en su estructura en Latinoam&eacute;rica, denominado viaducto Batalla de Niquitao.</p>\r\n<p><strong>La Excursi&oacute;n</strong></p>\r\n<p>Este es un ascenso bastante largo, por lo tanto se recomienda hacerlo en tres d&iacute;as, para poder disfrutar m&aacute;s de la belleza de estos p&aacute;ramos sin tener la presi&oacute;n del tiempo.</p>\r\n<p><strong>D&iacute;a 1: Bocono - Niquitao - Base de la Teta de Niquitao</strong></p>\r\n<p>El inicio de esta aventura comienza en Bocon&oacute;, donde se debe llegar al Terminal de Buses para poder tomar el transporte r&uacute;stico hasta el pueblo de Niquitao. El recorrido de Bocon&oacute; a Niquitao es como de una hora por una carretera seriamente da&ntilde;ada por derrumbes y el descuido. Una vez en el pueblo de Niquitao ubicado a 1937 msnm, se debe bajar en la Plaza Bol&iacute;var, donde se encuentra la iglesia. Aqu&iacute; se puede comprar algunas cosas que falten para el ascenso, tomar algunas fotos y disfrutar del clima del pueblo.</p>\r\n<p>El camino inicia con el descenso al r&iacute;o Burate que est&aacute; cien metros m&aacute;s abajo de la meseta de Niquitao y se cruza a trav&eacute;s del puente de hierro Jos&eacute; F&eacute;lix Ribas. De aqu&iacute; ven&iacute;a una subida de ladrillos muy empinada y despu&eacute;s de una hora de ascenso se encuentra la escuela del pueblo de Chejend&eacute;. El camino es para veh&iacute;culos de doble tracci&oacute;n y no es tan empinado. Hay que ir preguntando en las casas a los lados del camino ya que no hay ninguna se&ntilde;alizaci&oacute;n y hay muchas desviaciones. Lo interesante de este recorrido es que se puede apreciar los sembrad&iacute;os de papa, zanahoria y cebolla.</p>\r\n<p>A medida que se asciende, la distancia de las casas es m&aacute;s amplia hasta que empiezan a aparecer los frailejones y a lo lejos se vislumbra la silueta de la Teta de Niquitao. Existen atajos que hacen ahorrar un gran tiempo, ya que el camino de los r&uacute;sticos bordea las monta&ntilde;as. Lo ideal es llegar el primer d&iacute;a hasta la base de la Teta de Niquitao, donde hay una laguna y algunos espacios planos donde se puede acampar. El ascenso desde Niquitao hasta el campamento base, es un recorrido largo y se realiza en aproximadamente 7 horas.</p>\r\n<p><strong>D&iacute;a 2: Campo Base - Teta de Niquitao - Campo Base</strong></p>\r\n<p>Una vez en el campamento base, se debe bordear la laguna por la izquierda y seguir un camino bien discreto que asciende por peque&ntilde;o valle hasta un collado. Luego de un rato de ascenso se llega a un valle donde se puede apreciar una peque&ntilde;a cueva y de aqu&iacute; se aprecia el picacho. El ascenso contin&uacute;a hasta encontrase en la fila donde aparece la inmensa Laguna Las Mar&iacute;as. Desde este punto se bordea el camino por la izquierda y comienza una peque&ntilde;a trepada por la piedra hasta la cumbre de la Teta de Niquitao a 4006 msnm. Desde aqu&iacute; se puede observar el Pico Guirigay, la Sierra de La Culata, el Lago de Maracaibo y p&aacute;ramo por todos lados. El descenso es por la misma v&iacute;a hasta el campamento base. Una vez en el campamento base se recomienda descansar y recorrer los alrededores.</p>\r\n<p><strong>D&iacute;a 3: Campo Base - Niquitao - Las Mesitas - Bocono</strong></p>\r\n<p>El descenso de la monta&ntilde;a se puede realizar al pueblo de Niquitao o al pueblo Las Mesitas. Se recomienda descender al pueblo de Las Mesitas ya que se encuentra a mayor altura y por ende el descenso es menor. Por otra parte se visita este m&iacute;stico pueblo, donde pareciera que uno regresa en el tiempo. En el pueblo de Las Mesitas hay transporte para Bocon&oacute;.</p>', 'f', '1', 19, 1, NULL, 1, '2011-06-02 15:22:37', 0, 0);
INSERT INTO `contenidos` (`id`, `titulo`, `contenido`, `fijo`, `status`, `orden`, `tipo`, `padre`, `comentarios`, `fecha`, `orden2`, `home`) VALUES
(128, 'Roraima', '<p>El Tepuy Roraima, tambi&eacute;n llamado Monte Roraima o simplemente Roraima, es una monta&ntilde;a de formaci&oacute;n sedimentaria caracter&iacute;stica del Macizo Guayan&eacute;s, en forma de meseta o &quot;Tepuy&quot;. Su punto m&aacute;s alto se encuentra alrededor de los 2.723 msnm y su superficie es de 34,38 Km cuadrados aproximadamente.</p>\r\n<table align="center" cellspacing="0" cellpadding="0">\r\n    <tbody>\r\n        <tr>\r\n            <td><img width="720" vspace="5" hspace="10" height="350" align="left" border="0" alt="" title="Vista aerea del Roraima" src="img/rutas/roraima_port.jpg" /></td>\r\n        </tr>\r\n        <tr>\r\n            <td class="fotos_contenido_port">Vista aerea del Roraima</td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<table width="170" align="right" cellspacing="0" cellpadding="0">\r\n    <tbody>\r\n        <tr>\r\n            <td style="text-align: center;"><a href="?albumid=6048180397685707921&amp;photoid=6048180407325994210&amp;id=includes/galeria.php"><img width="160" vspace="5" hspace="10" height="160" align="middle" border="0" alt="" title="Letrero con las distancias" src="img/rutas/roraima_01.jpg" /></a></td>\r\n        </tr>\r\n        <tr>\r\n            <td class="fotos_contenido_lat"><a href="?albumid=6048180397685707921&amp;photoid=6048180407325994210&amp;id=includes/galeria.php">Letrero con las distancias</a></td>\r\n        </tr>\r\n        <tr>\r\n            <td style="text-align: center;"><a href="?albumid=6048180397685707921&amp;photoid=6048180399259433474&amp;id=includes/galeria.php"><img width="160" vspace="5" hspace="10" height="160" align="middle" border="0" alt="" title="Kioskos a orilla de la carretera" src="img/rutas/roraima_02.jpg" /></a></td>\r\n        </tr>\r\n        <tr>\r\n            <td class="fotos_contenido_lat"><a href="?albumid=6048180397685707921&amp;photoid=6048180399259433474&amp;id=includes/galeria.php">Kioskos a orilla de la carretera</a></td>\r\n        </tr>\r\n        <tr>\r\n            <td style="text-align: center;"><a href="?albumid=6048180397685707921&amp;photoid=6048180439245868130&amp;id=includes/galeria.php"><img width="160" vspace="5" hspace="10" height="160" align="middle" border="0" alt="" title="Inparques" src="img/rutas/roraima_03.jpg" /></a></td>\r\n        </tr>\r\n        <tr>\r\n            <td class="fotos_contenido_lat"><a href="?albumid=6048180397685707921&amp;photoid=6048180439245868130&amp;id=includes/galeria.php">Inparques</a></td>\r\n        </tr>\r\n        <tr>\r\n            <td style="text-align: center;"><a href="?albumid=6048180397685707921&amp;photoid=6048180490486393074&amp;id=includes/galeria.php"><img width="160" vspace="5" hspace="10" height="160" align="middle" border="0" alt="" title="Grupo antes de partir" src="img/rutas/roraima_04.jpg" /></a></td>\r\n        </tr>\r\n        <tr>\r\n            <td class="fotos_contenido_lat"><a href="?albumid=6048180397685707921&amp;photoid=6048180490486393074&amp;id=includes/galeria.php">Con las dos moles de fondo</a></td>\r\n        </tr>\r\n        <tr>\r\n            <td style="text-align: center;"><a href="?albumid=6048180397685707921&amp;photoid=6048180510537941746&amp;id=includes/galeria.php"><img width="160" vspace="5" hspace="10" height="160" align="middle" border="0" alt="" title="Caminata de aproximacion" src="img/rutas/roraima_05.jpg" /></a></td>\r\n        </tr>\r\n        <tr>\r\n            <td class="fotos_contenido_lat"><a href="?albumid=6048180397685707921&amp;photoid=6048180510537941746&amp;id=includes/galeria.php">Caminata de aproximacion</a></td>\r\n        </tr>\r\n        <tr>\r\n            <td style="text-align: center;"><a href="?albumid=6048180397685707921&amp;photoid=6048180614933181922&amp;id=includes/galeria.php"><img width="160" vspace="5" hspace="10" height="160" align="middle" border="0" alt="" title="Las dos moles" src="img/rutas/roraima_06.jpg" /></a></td>\r\n        </tr>\r\n        <tr>\r\n            <td 